import React, { useState } from 'react';
import ThemeContext from './ThemeContext';

const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    const body = document.body;
    if (theme === 'dark') {
      setTheme('light');
      applyLightTheme();
    } else {
      setTheme('dark');
      applyDarkTheme();
    }
  };

  const applyLightTheme = () => {
    document.documentElement.style.setProperty('--background-dark', '#ffffff');
    document.documentElement.style.setProperty('--background-light', '#141414');
    document.documentElement.style.setProperty('--text-dark', '#141414');
    document.documentElement.style.setProperty('--text-light', '#ffffff');
  };

  const applyDarkTheme = () => {
    document.documentElement.style.setProperty('--background-dark', '#141414');
    document.documentElement.style.setProperty('--background-light', '#ffffff');
    document.documentElement.style.setProperty('--text-dark', '#ffffff');
    document.documentElement.style.setProperty('--text-light', '#141414');
  }

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeProvider;
