import React, { useState, useEffect } from 'react';
import ThemeContext from './ThemeContext';

const themeStyles = {
  dark: {
    body: '#141414',
    text: '#ffffff',
    container: '#141414',
    photo: '#363636',
    apre: '#ffffff',
    boxShadow: '14px -14px 28px #1f1f1f, -14px 14px 28px #414141'
  },
  light: {
    body: '#ffffff',
    text: '#141414',
    container: '#ffffff',
    photo: '#cfccbb',
    apre: '#363636',
    boxShadow: '14px -14px 49px #707070,-14px 14px 49px #e8e8e8'
  }
};

const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    applyTheme();
  }, [theme]);

  const applyTheme = () => {
    const elements = document.querySelectorAll('.textAboutMe, .ContainerAboutMe, .ContainerPhoto, .Photo-img, .ContainerApre');
    elements.forEach(el => {
      const styles = themeStyles[theme];
      el.style.backgroundColor = styles.container;
      if (el.classList.contains('textAboutMe')) el.style.color = styles.text;
      if (el.classList.contains('Photo-img')) el.style.boxShadow = styles.boxShadow;
    });
    document.body.style.backgroundColor = themeStyles[theme].body;
  };

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'dark' ? 'light' : 'dark'));
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeProvider;
