import React, { useState } from 'react';
import ThemeContext from './ThemeContext';

const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light');

    const applyDarkTheme = (body) => {
      document.body.style.setProperty('background', '#141414');
      document.body.style.setProperty('--text-dark', '#ffffff');
    }
    const applyLightTheme = (body) => {
        document.body.style.setProperty('background', '#ffffff');
        document.body.style.setProperty('--text-light', '#141414');
        console.log(document.body.style)
      }

  const toggleTheme = () => {
    console.log(document.body.style)
    const body = document.body;
    if (theme === 'dark') {
      setTheme('light');
      applyLightTheme(body);
    } else {
      setTheme('dark');
      applyDarkTheme(body);
    }
  };


  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeProvider;
