import React, { useState } from 'react';
import Photo from '../../assets/Miguel.png'
import './Home.css';
import { useInView } from 'react-intersection-observer'

function Home() {
    const [ref, inView] = useInView({
        threshold: 0.1,
    });

    const [isVisible, setIsVisible] = useState(false);

    if (inView && !isVisible) {
        setIsVisible(true);
    }

    return (
        <div ref={ref} className='ContainerMain'>
            <div className={isVisible ? 'ContainerAboutMe' : 'ContainerAboutMe init-hidden'}>
                <h2> Sobre mim:</h2>
                <p>"Lorem ipsum dolor sit amet, ..."</p>
            </div>
            <div className={isVisible ? 'ContainerPhoto' : 'ContainerPhoto init-hidden'}>
                <a className='ImagemLink' href="https://www.linkedin.com/in/miguelazuoss/" target="_blank" rel="noopener noreferrer">
                    <img src={Photo} alt="Descrição da sua foto" className='Photo-img' />
                </a>   
            </div>
        </div>
    )
}

export default Home;
